import 'package:flutter/material.dart';import 'package:news_round/pages/splashScreen.dart';class AppLayoutBuilder extends StatelessWidget {  @override  Widget build(BuildContext context) {    return LayoutBuilder(builder: (context,constraint){      if(constraint.maxWidth <= 600){        return splashScreen();      }else{        return const Scaffold(          body:  Center(            child: Text('Screen is not compatible',              style: TextStyle(                fontSize: 25,                 fontStyle: FontStyle.normal,                fontFamily: 'nunitosSans_bold',                fontWeight: FontWeight.bold,                color: Colors.red,              ),            ),          ),        );      }    });  }}