import 'package:flutter/material.dart';import 'package:material_design_icons_flutter/material_design_icons_flutter.dart';//Bookmark added snackbarvoid bookmarkAdded(BuildContext context, bool lightTheme) {  ScaffoldMessenger.of(context).showSnackBar(SnackBar(    //Title of SnackBar    content: Row(      mainAxisAlignment: MainAxisAlignment.spaceBetween,      crossAxisAlignment: CrossAxisAlignment.center,      children: [        Text(          'Bookmark added',          style: TextStyle(            color: lightTheme ? Colors.black87 : Colors.white,            fontFamily: 'nunitosSans_semiBold',            fontSize: 17,            overflow: TextOverflow.ellipsis,          ),        ),        Icon(          Icons.check_circle_sharp,          color: Colors.green,        )      ],    ),    showCloseIcon: false,    backgroundColor:lightTheme? Colors.white:Colors.black,    behavior: SnackBarBehavior.floating,    margin: const EdgeInsets.only(left: 20, right: 20, bottom: 20),    duration:const Duration(seconds: 1),  ));}//Bookmark Removed snackbarvoid bookmarkRemoved(BuildContext context, bool lightTheme) {  ScaffoldMessenger.of(context).showSnackBar(SnackBar(    //Title of SnackBar    content: Row(      mainAxisAlignment: MainAxisAlignment.spaceBetween,      crossAxisAlignment: CrossAxisAlignment.center,      children: [        Text(          'Bookmark removed',          style: TextStyle(            color: lightTheme ? Colors.black87 : Colors.white,            fontFamily: 'nunitosSans_semiBold',            fontSize: 17,            overflow: TextOverflow.ellipsis,          ),        ),        Icon(          MdiIcons.closeCircle,          color: Colors.red,        )      ],    ),    showCloseIcon: false,    backgroundColor: lightTheme? Colors.white:Colors.black,    behavior: SnackBarBehavior.floating,    margin: const EdgeInsets.only(left: 20, right: 20, bottom: 20),    duration:const Duration(seconds: 1),  ));}