import 'dart:convert';import 'package:http/http.dart' as http;import 'package:news_round/models/newsHeadlinesAPImodel.dart';class newsHeadlineRepo{  //Repo => For newsHeadlinesAPI  Future<newsHeadlinesAPImodel> fechNewsHeadlines(String? url) async{    final response = await http.get(Uri.parse(url!)); //Parsing the API get data    var data = jsonDecode(response.body); //Decoding the body of received data    //When call successful    if(response.statusCode==200){      return newsHeadlinesAPImodel.fromJson(data);    }else if(response.statusCode ==403){      throw('Error: something went wrong with image & Status code is ${response.statusCode.toString()}');    }else{      throw('Error: something went wrong');    }  }}